package Arrays;

import java.util.Arrays;

public class CompareArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr1[] = { 1, 2, 3, 4};
		int arr2[] = { 1, 2, 3, 4};
		
		if (Arrays.equals(arr1, arr2))
			System.out.println("Same");
		
		else {
			System.out.println("Not Same");
			
		}

	}

}
